import{_ as a,c as t,o as i,aj as n}from"./chunks/framework.Bbpi2Fiw.js";const h=JSON.parse('{"title":"ADR-012 Explicit Restatement of Requirements in Backend Specs","description":"","frontmatter":{},"headers":[],"relativePath":"adr/012-backend-restatement-pattern.md","filePath":"adr/012-backend-restatement-pattern.md","lastUpdated":1764705171000}'),r={name:"adr/012-backend-restatement-pattern.md"};function s(c,e,l,d,o,p){return i(),t("div",null,[...e[0]||(e[0]=[n('<h1 id="adr-012-explicit-restatement-of-requirements-in-backend-specs" tabindex="-1">ADR-012 Explicit Restatement of Requirements in Backend Specs <a class="header-anchor" href="#adr-012-explicit-restatement-of-requirements-in-backend-specs" aria-label="Permalink to “ADR-012 Explicit Restatement of Requirements in Backend Specs”">​</a></h1><p><strong>Status:</strong> Accepted <strong>Date:</strong> 2025-10 <strong>Tags:</strong> documentation, machine-readability, consistency</p><h2 id="problem" tabindex="-1">Problem <a class="header-anchor" href="#problem" aria-label="Permalink to “Problem”">​</a></h2><p>ADR-010 and interface.md established authoritative <code>expiresAtMs</code> requirement, but backend specs didn&#39;t explicitly restate it as MUST bullets. Agents could miss critical requirements during compliance checks.</p><h2 id="decision" tabindex="-1">Decision <a class="header-anchor" href="#decision" aria-label="Permalink to “Decision”">​</a></h2><p>Backend specifications MUST restate key inherited requirements in operation sections:</p><ul><li>Add explicit MUST bullets to Acquire and Extend sections</li><li>Reference ADRs for rationale to avoid redundant prose</li><li>Backend Delta Pattern guidance in interface.md</li></ul><h2 id="alternatives-brief" tabindex="-1">Alternatives (brief) <a class="header-anchor" href="#alternatives-brief" aria-label="Permalink to “Alternatives (brief)”">​</a></h2><ul><li>Cross-reference only — agents miss requirements, drift risk</li><li>Full duplication — maintenance burden, inconsistency risk</li></ul><h2 id="impact" tabindex="-1">Impact <a class="header-anchor" href="#impact" aria-label="Permalink to “Impact”">​</a></h2><ul><li>Positive: Machine-parseable, agents verify from backend tables alone, prevents drift</li><li>Negative/Risks: Minor duplication (mitigated by cross-references)</li></ul><h2 id="links" tabindex="-1">Links <a class="header-anchor" href="#links" aria-label="Permalink to “Links”">​</a></h2><ul><li>Code/Docs: <code>docs/specs/README.md</code> (Backend Delta Pattern), backend specs</li><li>Related ADRs: ADR-010 (authoritative expiresAtMs)</li></ul>',13)])])}const u=a(r,[["render",s]]);export{h as __pageData,u as default};
